import React, { Component } from "react";
import styled from "styled-components";

class ShopCart extends Component {
  render() {
    const { cart, products, change } = this.props;
    // console.log(products[0].proName);
    // console.log(cart);
    const shopCart =
      cart.length && products.length ? (
        <ul className="cart">
          {cart.map(ele => (
            <li key={ele.id}>
              <p className="proName">
                {products.find(e => e.id === ele.proId).proName} :{" "}
              </p>
              <button
                disabled={ele.num <= 1}
                onClick={() => {
                  change(ele.id, ele.num - 1);
                }}
              >
                -
              </button>
              <p>{ele.num}</p>
              <button
                disabled={
                  ele.num > products.find(e => e.id === ele.proId).inventory
                }
                onClick={() => {
                  change(ele.id, ele.num + 1);
                }}
              >
                +
              </button>
            </li>
          ))}
        </ul>
      ) : (
        "购物车中暂无商品"
      );
    return (
      <Cart>
        <h4>购物车：</h4>
        {shopCart}
      </Cart>
    );
  }
}

export default ShopCart;
const Cart = styled.div`
  .cart > li {
    display: flex;
    align-items: center;
  }
  button {
    width: 30px;
    height: 30px;
  }
  .proName {
    width: 150px;
  }
`;
