import React, { Component } from "react";
import ProductList from "./ProductList";
import { connect } from "react-redux";
import { getPro, addCart, change, minus } from "./../actions";
import ShopCart from "./ShopCart";

class Cart extends Component {
  componentDidMount() {
    const { getPro } = this.props;
    getPro();
    // getCart({
    //   productId: ["1"],
    //   quantityByid: { "1": 1 }
    // });
  }
  render() {
    // console.log(this.props);
    const { products, addCart, cart, change } = this.props;
    // console.log(products);
    return (
      <div>
        <ProductList products={products} addCart={addCart} />
        <ShopCart cart={cart} products={products} change={change} />
      </div>
    );
  }
}

const mapStatetoProps = state => ({
  products: state.products,
  cart: state.cart
});

export default connect(
  mapStatetoProps,
  { getPro, addCart, change }
)(Cart);
