import React, { Component } from "react";
import ProductList from "./ProductList";
import { connect } from "react-redux";
import { getPro, addCart, getCart } from "./../actions";
import ShopCart from "./ShopCart";

class Cart extends Component {
  componentDidMount() {
    const { getPro, getCart } = this.props;
    getPro();
    getCart();
  }
  render() {
    // console.log(this.props);
    const { products, addCart, cart, change } = this.props;
    console.log(products);
    return (
      <div>
        <ProductList products={products} addCart={addCart} />
        <ShopCart cart={cart} products={products} change={change} />
      </div>
    );
  }
}

const mapStatetoProps = state => ({
  products: state.products,
  cart: state.cart
});

export default connect(
  mapStatetoProps,
  { getPro, addCart, getCart, change }
)(Cart);
