const cart = (state = [], action) => {
  switch (action.type) {
    case "GET_CART":
      return action.cart;
      break;
    case "CHANGE":
      return state.map(ele => {
        if (ele.id === action.newNUmInvent.id) {
          return action.newInvent;
        } else {
          return ele;
        }
      });
      break;
    default:
      return state;
  }
};

export default cart;
