import axios from "axios";

export const getPro = () => {
  return dispatch => {
    axios.get("http://localhost:2019/products").then(res => {
      dispatch({
        type: "GET_PRO",
        products: res.data
      });
    });
  };
};
export const addCart = (id, num) => {
  console.log(num);
  return dispatch => {
    axios
      .patch(`http://localhost:2019/products/${id}`, { inventory: num })
      .then(res => {
        console.log(res.data);
        dispatch({
          type: "ADD_CART",
          newInvent: res.data
        });
      });
  };
};
export const getCart = (id, num) => {
  console.log(num);
  return dispatch => {
    axios.get(`http://localhost:2019/cart/`).then(res => {
      console.log(res.data);
      dispatch({
        type: "GET_CART",
        cart: res.data
      });
    });
  };
};
export const change = (id, num) => {
  console.log(num);
  return dispatch => {
    axios.patch(`http://localhost:2019/cart/${id}`, { num: num }).then(res => {
      console.log(res.data);
      dispatch({
        type: "CHANGE",
        newNum: res.data
      });
    });
  };
};
