import axios from "axios";

export const getPro = () => {
  return dispatch => {
    axios.get("http://localhost:2019/products").then(res => {
      dispatch({
        type: "GET_PRO",
        products: res.data
      });
    });
  };
};
export const addCart = (id, num) => {
  return dispatch => {
    axios
      .patch(`http://localhost:2019/products/id=${id}`, { num: num-- })
      .then(res => {
        console.log(res.data);
        dispatch({
          type: "ADD_CART",
          products: res.data
        });
      });
  };
};
