const cart = (
  state = {
    productId: [],
    quantityByid: {}
  },
  action
) => {
  switch (action.type) {
    case "GET_CART":
      return action.cart;
      break;
    case "CHANGE":
      return state.productId.map(ele => {
        if (ele === action.id) {
          console.log(state.quanitityByid);

          // state.quantityByid[ele] = action.newNum;
        }
        return ele;
      });
      break;
    default:
      return state;
  }
};

export default cart;
