export const change = id => {
  return dispatch => {
    dispatch({
      type: "CHANGE",
      id
    });
    dispatch({
      type: "ADD_CART",
      id
    });
  };
};
export const minus = (proid, num) => {
  axios
    .patch(`http://localhost:2019/products/${proid}`, { inventory: num })
    .then(res => {
      // console.log(res.data);
      return dispatch => {
        dispatch({
          type: "MINUS",
          proid
        });
        dispatch({
          type: "REMOVE_CART",
          proid
        });
      };
    });
};

export const checkout = () => {
  return dispatch => {
    dispatch({
      type: "CHECK_OUT",
      checkout: {
        productId: [],
        quantityByid: {}
      }
    });
  };
};
