import React, { Component } from "react";

class ShopCart extends Component {
  render() {
    const { cart, products } = this.props;
    console.log(cart);
    const shopCart = cart.length ? (
      <ul>
        {cart.map(ele => (
          <li key={ele.id}>
            <p>{products.find(e => e.id === ele.proId).name}</p>
            <button disabled={ele.num > 1}>-</button>
            <p>{ele.num}</p>
            <button
              disabled={ele.num > products.find(e => e.id === ele.proId).name}
            >
              +
            </button>
          </li>
        ))}
      </ul>
    ) : (
      "购物车中暂无商品"
    );
    return <div>shopCart</div>;
  }
}

export default ShopCart;
