const products = (state = [], action) => {
  switch (action.type) {
    case "GET_PRO":
      return action.products;
      break;
    case "ADD_CART":
      const theState = state;
      return state.map(ele => {
        if (ele.id === action.id) {
          ele.inventory--;
          return ele;
        } else {
          return ele;
        }
      });
      break;
    case "REMOVE_CART":
      const newState = state;
      return state.map(ele => {
        if (ele.id === action.id) {
          ele.inventory++;
          return ele;
        } else {
          return ele;
        }
      });
      break;
    default:
      return state;
  }
};

export default products;
