import React, { Component } from "react";

class ShopCart extends Component {
  render() {
    const { cart, products } = this.props;
    // console.log(products[0].proName);
    console.log(cart);
    const shopCart = cart.length ? (
      <ul className="cart">
        {cart.map(ele => (
          <li key={ele.id}>
            <p>
              {products
                ? products.find(e => e.id === ele.proId).proName
                : "加载……"}
            </p>
            <button disabled={ele.num <= 1}>-</button>
            <p>{ele.num}</p>
            <button
            // disabled={
            //   // ele.num > products.find(e => e.id === ele.proId).inventory
            // }
            >
              +
            </button>
          </li>
        ))}
      </ul>
    ) : (
      "购物车中暂无商品"
    );
    return <div>{shopCart}</div>;
  }
}

export default ShopCart;
const Cart = styled.div`
  .cart > li {
    display: flex;
  }
`;
