// state = {
//   productId: [],
//   quantityByid: {}
// }
const cart = (
  state = {
    productId: [],
    quantityByid: {}
  },
  action
) => {
  switch (action.type) {
    case "GET_CART":
      return action.cart;
      break;
    case "CHANGE":
      const { id } = action;
      const newState = { ...state };
      if (cart.productId.indexOf(action.id) === -1) {
        newState.productId.push(action.id);
        newState.quantityByid[id] = 1;
      } else {
        newState.quantityByid[id]++;
      }
      return newState;
      break;
    default:
      return state;
  }
};

export default cart;
